package timelock

import (
	agbinary "github.com/gagliardetto/binary"
)

// This Errors should be automatically generated by Anchor-Go but they only support one error per program

// AuthError matches the AuthError enum in contracts/programs/timelock/src/error.rs
type AuthError agbinary.BorshEnum

const (
	UnauthorizedError AuthError = iota
)

func (value AuthError) String() string {
	switch value {
	case UnauthorizedError:
		return "Unauthorized"
	default:
		return ""
	}
}

// TimelockError matches the TimelockError enum in contracts/programs/timelock/src/error.rs
type TimelockError agbinary.BorshEnum

const (
	InvalidInputTimelockError TimelockError = iota
	OverflowTimelockError
	InvalidIdTimelockError
	OperationNotFinalizedTimelockError
	OperationAlreadyFinalizedTimelockError
	TooManyInstructionsTimelockError
	OperationAlreadyScheduledTimelockError
	DelayInsufficientTimelockError
	OperationNotCancellableTimelockError
	OperationNotReadyTimelockError
	OperationAlreadyExecutedTimelockError
	MissingDependencyTimelockError
	InvalidAccessControllerTimelockError
	BlockedSelectorTimelockError
	AlreadyBlockedTimelockError
	SelectorNotFoundTimelockError
	MaxCapacityReachedTimelockError
)

func (value TimelockError) String() string {
	switch value {
	case InvalidInputTimelockError:
		return "InvalidInput"
	case OverflowTimelockError:
		return "Overflow"
	case InvalidIdTimelockError:
		return "InvalidId"
	case OperationNotFinalizedTimelockError:
		return "OperationNotFinalized"
	case OperationAlreadyFinalizedTimelockError:
		return "OperationAlreadyFinalized"
	case TooManyInstructionsTimelockError:
		return "TooManyInstructions"
	case OperationAlreadyScheduledTimelockError:
		return "OperationAlreadyScheduled"
	case DelayInsufficientTimelockError:
		return "DelayInsufficient"
	case OperationNotCancellableTimelockError:
		return "OperationNotCancellable"
	case OperationNotReadyTimelockError:
		return "OperationNotReady"
	case OperationAlreadyExecutedTimelockError:
		return "OperationAlreadyExecuted"
	case MissingDependencyTimelockError:
		return "MissingDependency"
	case InvalidAccessControllerTimelockError:
		return "InvalidAccessController"
	case BlockedSelectorTimelockError:
		return "BlockedSelector"
	case AlreadyBlockedTimelockError:
		return "AlreadyBlocked"
	case SelectorNotFoundTimelockError:
		return "SelectorNotFound"
	case MaxCapacityReachedTimelockError:
		return "MaxCapacityReached"
	default:
		return ""
	}
}
